from flask_restful import fields
from flask_restful_swagger import swagger


@swagger.model
class TenantResponse(object):

    resource_fields = {
        'name': fields.String,
        'groups': fields.Raw,
        'users': fields.Raw,
    }

    def __init__(self, **kwargs):
        self.name = kwargs.get('name')
        self.groups = kwargs.get('groups')
        self.users = kwargs.get('users')


@swagger.model
class GroupResponse(object):

    resource_fields = {
        'name': fields.String,
        'tenants': fields.Raw,
        'users': fields.Raw,
        'ldap_dn': fields.Raw
    }

    def __init__(self, **kwargs):
        self.name = kwargs.get('name')
        self.tenants = kwargs.get('tenants')
        self.users = kwargs.get('users')


@swagger.model
class UserResponse(object):

    resource_fields = {
        'username': fields.String,
        'tenants': fields.Raw,
        'groups': fields.Raw,
        'role': fields.String,
        'active': fields.Boolean,
        'last_login_at': fields.String
    }

    def __init__(self, **kwargs):
        self.username = kwargs.get('username')
        self.tenants = kwargs.get('tenants')
        self.groups = kwargs.get('groups')
        self.role = kwargs.get('role')
        self.active = kwargs.get('active')
        self.last_login_at = kwargs.get('last_login_at')


@swagger.model
class LdapResponse(object):

    resource_fields = {
        'ldap_server': fields.String,
        'ldap_username': fields.String,
        'ldap_domain': fields.String,
        'ldap_is_active_directory': fields.Boolean,
        'ldap_dn_extra': fields.String
    }

    def __init__(self, **kwargs):
        self.ldap_server = kwargs.get('ldap_server')
        self.ldap_username = kwargs.get('ldap_username')
        self.ldap_domain = kwargs.get('ldap_domain')
        self.ldap_is_active_directory = kwargs.get('ldap_is_active_directory')
        self.ldap_dn_extra = kwargs.get('ldap_dn_extra')
