from functools import wraps

from .multi_tenancy import MultiTenancy

from manager_rest.security.secured_resource import (
    SecuredResource,
    SecuredResourceSkipTenantAuth
)


def multi_tenant_handled(func):
    @wraps(func)
    def wrapper(*args, **kwargs):
        return func(*args, multi_tenancy=MultiTenancy(), **kwargs)
    return wrapper


class SecuredMultiTenancyResource(SecuredResource):
    method_decorators = SecuredResource.method_decorators + \
                        [multi_tenant_handled]


class SecuredMultiTenancyResourceSkipTenantAuth(SecuredResourceSkipTenantAuth):
    method_decorators = SecuredResourceSkipTenantAuth.method_decorators + \
                        [multi_tenant_handled]
